const filterBtn = document.querySelector(".content-blog__filter"),
  filterContent = document.querySelector(".filter__content");
filterBtn.addEventListener("click", () => {
  filterBtn.classList.toggle("active"),
    filterContent.classList.toggle("filter__content--open");
}),
  document.querySelectorAll(".dropdown").forEach((e) => {
    const t = e.querySelector(".dropdown__button"),
      o = e.querySelector(".dropdown__list"),
      n = o.querySelectorAll(".dropdown__list-item"),
      d = e.querySelector(".dropdown__input-hidden");
    t.addEventListener("click", function () {
      o.classList.toggle("dropdown__list-visible"),
        this.classList.toggle("dropdown__button--active");
    }),
      n.forEach((e) => {
        e.addEventListener("click", function (e) {
          e.stopPropagation(),
            (t.innerText = this.innerText),
            t.focus(),
            (d.value = this.dataset.value),
            o.classList.remove("dropdown__list-visible");
        });
      }),
      document.addEventListener("click", (e) => {
        e.target !== t &&
          (o.classList.remove("dropdown__list-visible"),
          t.classList.remove("dropdown__button--active"));
      }),
      document.addEventListener("keydown", (e) => {
        ("Tab" !== e.key && "Escape" !== e.key) ||
          (o.classList.remove("dropdown__list-visible"),
          t.classList.remove("dropdown__button--active"));
      });
  });
const buttons = document.querySelectorAll(".more-btn"),
  hiddenCards = document.querySelectorAll("[data-card]");
buttons.forEach((e) => {
  e.addEventListener("click", (t) => {
    t.preventDefault(),
      hiddenCards.forEach((t) => {
        "hidden" === t.dataset.card
          ? (t.classList.remove("bounceOut"),
            t.classList.add("fadeInUp"),
            setTimeout(() => {
              t.style.display = "block";
            }, 200),
            (e.textContent = "Скрыть"),
            (t.dataset.card = "open"))
          : "open" === t.dataset.card &&
            (t.classList.remove("fadeInUp"),
            t.classList.add("bounceOut"),
            (e.textContent = "Показать больше статей"),
            (t.dataset.card = "hidden"),
            setTimeout(() => {
              t.style.display = "none";
            }, 500));
      });
  });
});
const clearFilterBtn = document.querySelector(".filter__content-btn"),
  radioButtons = document.querySelectorAll("input[type='radio']"),
  checkboxButtons = document.querySelectorAll("input[type='checkbox']"),
  filterAddBox = document.querySelector(".filter__addbox");
setInterval(() => {
  const e = document.querySelectorAll(".filter__add");
  for (const t of e)
    t.addEventListener("click", (e) => {
      e.target.remove();
    });
}, 1e3),
  clearFilterBtn.addEventListener("click", () => {
    for (const e of radioButtons) e.checked = !1;
    for (const e of checkboxButtons) e.checked = !1;
    document.querySelectorAll(".filter__add").forEach((e) => {
      e.remove();
      for (const e of radioButtons) e.checked = !1;
      for (const e of checkboxButtons) e.checked = !1;
    });
  });
for (const e of radioButtons)
  e.addEventListener("click", () => {
    const t = e.nextElementSibling.innerText,
      o = document.createElement("div");
    o.classList.add("filter__add"),
      (o.textContent = t),
      filterAddBox.appendChild(o);
  });
for (const e of checkboxButtons)
  e.addEventListener("click", () => {
    const t = e.nextElementSibling.nextElementSibling.innerText,
      o = document.createElement("div");
    o.classList.add("filter__add"),
      (o.textContent = t),
      filterAddBox.appendChild(o);
  });
